import React,{useState} from 'react'
import Tile from './Tile'
const Board = () => {
  //létre hozzuk a boardot
  const length = 5;
  const height = 5;
  var gfg = new Array(2);
  for (var i = 0; i < gfg.length; i++) {
    gfg[i] = new Array(2);
}
  
var h = 0;
  
// Loop to initialize 2D array elements.
for (var i = 0; i < 2; i++) {
    for (var j = 0; j < 2; j++) {
        gfg[i][j] = h++;
    }
}


  const [board,setBoard] = useState(gfg);

  const handleTileClick = (position)=>{
    //ha már van értéke a mezőnek visszatér
    /*if(board[position]){
      return;
    }*/
     setBoard((prev)=> {

      return prev.map((tile,pos) => {
        if(pos === position){         
        return 'X';
        }
        return tile;
      })
     })
  };

  const renderTile = (position) =>{
    return(
       <Tile
        value={board[position]}
         onClick={()=> handleTileClick(position)}
         />
    );
  };
  
  return (
    <div className='board'>
        <div className='board-row'>
            {renderTile([0][1])}
            {renderTile(1)}
            {renderTile(2)}
            <span className='side-numbers'>1</span>
        </div>
        <div className='board-row'>
          {renderTile(3)}
          {renderTile(4)}
          {renderTile(5)}
          <span className='side-numbers'>1</span>
        </div>
        <div className='board-row'>
          {renderTile(6)}
          {renderTile(7)}
          {renderTile(8)}
          <span className='side-numbers'>1</span>
           </div>
    </div>
  )
}

export default Board
