import React,{useState, useEffect, useContext} from 'react';
import Axios from "axios";
import { ChosenMap } from '../ChosenMap';
import { Link  } from 'react-router-dom';
import { useHistory   } from "react-router-dom";
import Game from '../../pages/Game'
const MapsTable = () =>  {
    const history = useHistory();
    const [mapList, setMapList] = useState([])
    const {chosenMap, setChosenMap} = useContext(ChosenMap);
useEffect(()=>{
    Axios.get('http://localhost:3001/api/getMaps').then((response) =>{
    setMapList(response.data);
    }); 
},[]);
useEffect(() => {
  // code to run after render goes here
  const tableEl = document.querySelector('table');
  tableEl.addEventListener('click',onClickPlay);
  function onClickPlay(e){
    if(tableEl !== null){
      const btn = e.target;
      const row = btn.closest("tr");
      setChosenMap(row.cells[0].innerHTML)
    }
  }
},[]);
const test =()=>{
  console.log(chosenMap)
  history.push('/game')
 
}
let tb_data = mapList.map((item)=>{
 return (
   <tr key={item.id}>
     <td>{item.id}</td>
     <td>{item.name}</td>
     <td>{item.width}</td>
     <td>{item.height}</td>
     <td>{item.uploaded_by}</td>
     <td>
      <button className='playBtn' onClick={()=>test()}>PLay</button>
     </td>
   </tr>
 )
})
  return(
    <div>  
        <table style={style.tableStyle}>
          <thead>
            <tr>
              <th>id</th>
              <th>name</th>
              <th>width</th>
              <th>height</th>
              <th>uploaded_by</th>
            </tr>
          </thead>
          <tbody>
                {tb_data}
          </tbody>
        </table>
    </div>
  )
  const style = {
    tableStyle:{
      
        fontFamily: 'Arial',
        borderCollapse: 'collapse',
        width: '100',
    }
      
      
  }
}
export default MapsTable;
