import React,{useState, useEffect} from 'react';
import Axios from "axios";

const MapsTable = () =>  {
    const [mapList, setMapList] = useState([])

useEffect(()=>{
    Axios.get('http://localhost:3001/api/getMaps').then((response) =>{
    setMapList(response.data);
    });
},[]);
useEffect(() => {
  // code to run after render goes here
  const tableEl = document.querySelector('table');
tableEl.addEventListener('click',onClickPlay);
function onClickPlay(e){
if(!e.target.classList.contains('playBtn')){
  return
}
const btn = e.target;
const row = btn.closest("tr");
console.log(row[0].innerHTML)
}
});
let tb_data = mapList.map((item)=>{
 return (
   <tr key={item.id}>
     <td>{item.id}</td>
     <td>{item.name}</td>
     <td>{item.width}</td>
     <td>{item.height}</td>
     <td>{item.uploaded_by}</td>
     <td>
      <button className='playBtn'>PLay</button>
     </td>
   </tr>
 )
})
  return(
    <div>  
        <table>
          <thead>
            <tr>
              <th>id</th>
              <th>name</th>
              <th>width</th>
              <th>height</th>
              <th>uploaded_by</th>
            </tr>
          </thead>
          <tbody>
                {tb_data}
          </tbody>
        </table>
    </div>
  )
  
}
export default MapsTable;
