     setBoard((prev)=> {

      return prev.map((square,pos) => {
        if(pos === position){         
        return 'X';
        }
        return tile;
      })
     })
  };

  const renderTile = (position) =>{
    return(
       <Tile
        value={board[position]}
         onClick={()=> handleTileClick(position)}
         />
    );
  };
  
  return (
    <div className='board'>
        <div className='board-row'>
            {renderTile(0)}
            {renderTile(1)}
            {renderTile(2)}
        </div>
        <div className='board-row'>
          {renderTile(3)}
          {renderTile(4)}
          {renderTile(5)}
        </div>
        <div className='board-row'>
          {renderTile(6)}
          {renderTile(7)}
          {renderTile(8)}
           </div>
    </div>
  )
}

export default Board
