import React,{useState} from 'react'
import Tile from './Tile'
const Board = () => {
  //létre hozzuk a boardot
  const [board,setGrid] = useState([]);

  let  createBoard= (row, col) => {
    let board = [];
    
    // Create blank board
  
    // x = column
    for (let x = 0; x < row; x++) {
      let subCol = [];
      for (let y = 0; y < col; y++) {
        subCol.push({
          value: 0,
          revealed: false,
          x: x,
          y: y,
        });
      }
      board.push(subCol);
    }
  };

  
  
  return (
    <div className='board'>
       
    </div>
  )
}

export default Board
