import React,{useState} from 'react'
import Tile from './Tile'
const Board = () => {
  //létre hozzuk a boardot
  const length = 9;
  const height = 5;
  var grid = [];
  const iMax = 3;
  const jMax = 3;
  let count = 0;

    for (let i = 0; i < iMax; i++) {
      grid[i] = [];

      for (let j = 0; j < jMax; j++) {
        grid[i][j] = count;
        count++;
      }
    }

  const [board,setBoard] = useState(grid);

  const handleTileClick = (position)=>{
    //ha már van értéke a mezőnek visszatér
    /*if(board[position]){
      return;
    }*/
     setBoard((prev)=> {

      return prev.map((tile,pos) => {
        if(pos === position){         
        return 'X';
        }
        return tile;
      })
     })
  };

  const renderTile = (position) =>{
    return(
       <Tile
        value={board[0][1]}
         onClick={()=> handleTileClick(position)}
         />
    );
  };
  
  return (
    <div className='board'>
        <div className='board-row'>
            {renderTile(0)}
            {renderTile(1)}
            {renderTile(2)}
            <span className='side-numbers'>1</span>
        </div>
        <div className='board-row'>
          {renderTile(3)}
          {renderTile(4)}
          {renderTile(5)}
          <span className='side-numbers'>1</span>
        </div>
        <div className='board-row'>
          {renderTile(6)}
          {renderTile(7)}
          {renderTile(8)}
          <span className='side-numbers'>1</span>
           </div>
    </div>
  )
}

export default Board
