import React, {useState,Component, useRef, useEffect } from 'react';
import createBoardCreator from '../components/createPage/createBoardCreator';
import CreatorTile from '../components/createPage/CreatorTile';
import CreatorCell from '../components/createPage/CreatorCell';
import CreatorBottomCells from '../components/createPage/CreatorBottomCells';
import  Axios  from 'axios';
function Create() {
  const [activeCard, setActiveCard] = useState(0);
  const [board,setBoard] = useState([]);
  const [grid,setGrid] = useState([]);
  const [width,setWidth] = useState(3);
  const [height,setHeight] = useState(3);
  const [bodyPart,setBodyPart] = useState(0);
  const [lastPos,setLastPos] = useState([0,0]);
  const [savedBoard,setSavedBoard] = useState([]);
  const [buildStade, setBuildStade] = useState(0);

  useEffect(()=>{
  function freshBoard (row,col){
    const newBoard = createBoardCreator(row, col);
      setBoard(newBoard);
    setGrid(newBoard.board);
  }freshBoard(height,width)
},[height]);

  const tileClick = (x,y) =>{
    let newGrid = JSON.parse(JSON.stringify(grid));
    let newBoard = JSON.parse(JSON.stringify(board));
    let elozo = lastPos;

    if(bodyPart === 1){
      setLastPos([x,y])
    }
    if(bodyPart === 5){

      if(newGrid[x][y].value === 0 || newGrid[x][y].value === 3 || newGrid[x][y].value === 4 && x === elozo[0]){
        if(y + 1 === elozo[1] || y - 1 === elozo[1]){
            newGrid[x][y].value = 5;
            elozo[1] = y;
            setGrid(newGrid);

          }   
      }
      if(newGrid[x][y].value === 0 || newGrid[x][y].value === 3 || newGrid[x][y].value === 4 && y === elozo[1]){
          if(x + 1 === elozo[0] || x - 1 === elozo[0]){
            newGrid[x][y].value = 5;
            elozo[0] = x;
            setGrid(newGrid);
        }   
      }
      setLastPos(elozo);
    }
    else{
    newGrid[x][y].value = bodyPart;
    newBoard.board = newGrid;
    }
    setGrid(newGrid);
    setBoard(newBoard);
  }
  //bal click event
  const tileRightClick = (x,y) =>{
    let newGrid = JSON.parse(JSON.stringify(grid));
    let newBoard = JSON.parse(JSON.stringify(board));
    newGrid[x][y].value = 2;
    newBoard.board = newGrid;
    setGrid(newGrid);
    setBoard(newBoard);

  }
//el menti a testrészeket és át vált a test berajzolására
  const saveMap = () =>{
    let newGrid = JSON.parse(JSON.stringify(grid));
    let fej = 0
    let test = 0
    let farok = 0

    newGrid.map((singleRow,index1)=>{
      singleRow.map((singleBlock,index2)=>{
        if(singleBlock.value === 1 ){
          fej++
        }
        if(singleBlock.value === 3 ){
          test++
        }
        if(singleBlock.value === 4 ){
          farok++
        }
      })
    })
    if(fej === 1 && test === 1 && farok === 1){

      setSavedBoard(newGrid);
      setBuildStade(1)
    }
    else{
      alert('ther can only be 1 of every body part and there has to be one of every bodypart')

    }


  }


  const submitMap = () =>{
  //testrészek és kövek mentése
    let stones = ''
    let parts = ''
    let head = ''
    let body = ''
    let tail = ''
    savedBoard.map((singleRow,index1)=>{
      singleRow.map((singleBlock,index2)=>{
        if(singleBlock.value === 2 ){
          stones = stones + index1 + ',' + index2 + ','
        }
        if(singleBlock.value === 1 ){
          head = head + index1 + ',' + index2 + ','
        }
        if(singleBlock.value === 3 ){
          body = body + index1 + ',' + index2 + ','
        }
        if(singleBlock.value === 4 ){
          tail = tail + index1 + ',' + index2 + ','
        }
      })
    })
    parts = head + body + tail
    //le vesszük az utolso vesszőt a strindről 
    parts = parts.slice(0, -1);
    stones = stones.slice(0, -1);
//oldalak számolása
  //jobb oldal
    let right = []
    let bottom = []
    savedBoard.map((singleRow,index1)=>{
      let counter = 0;
      singleRow.map((singleBlock,index2)=>{
        if(grid[index1][index2].value === 5 || grid[index1][index2].value === 1){
          counter++;
        }
      })
      right.push(counter);
    })
    console.log(right)

  //bal oldal
    if(grid.length>1){
      for(let i = 0; i < width;i++){
        let colCount = 0; 
        for(let j = 0; j < height;j++){
          if(grid[j][i].value === 1||grid[j][i].value === 5){
            colCount++;
           } 
        }
        bottom.push(colCount);
      }
      console.log(bottom)
    }
  //oldalak stringé alakítása  
//map mentése
    Axios.post("http://localhost:3001/api/insertMap",{
      name:'test',
      width:width,
      height:height,
      parts:parts,
      stones:stones,
      right:right.toString(),
      bottom:bottom.toString(),
      uploaded_by:'bob',
  }).then(()=>{
    alert("success");
  });
  };

//rendelelés
//map méret bekérés
  if(activeCard === 0){
  return (
    <div className='create'>
      <div className='form'>
      <label>width :</label>
       <input type="number" name="width" onChange={event => setWidth(event.target.value)} />
       <label>height :</label>
       <input type="number" name="height"onChange={event => setHeight(event.target.value)} />


        <div>
        <button onClick={() => { setActiveCard(1) } }>Done</button>
        </div>
      </div>

    </div>
  )
  }
//kövek és test részek be rajzolása
  return( 
    <div className='create'>
    <div>
      {
        grid.map((singleRow,index1)=>{
          return <div style={{display: "flex"}} key={index1}>{
           singleRow.map((singleBlock,index2)=>{
         return <CreatorTile details={singleBlock} tileClick={tileClick} tileRightClick={tileRightClick} key={index2}/>
      })
      }

          </div> 
        })

      }

      {buildStade === 0
      ? <div className='container'>
        <button onClick={() =>setBodyPart(1)}>Head</button>
        <button onClick={() =>setBodyPart(3)}>Body</button>
        <button onClick={() =>setBodyPart(4)}>Tail</button>
        <button onClick={() =>setBodyPart(0)}>Delete</button>
        <div>
          <button onClick={() =>{saveMap();setBodyPart(5)}}>draw the snake</button>
      </div>
      </div>


     : <div>
      <button onClick={() =>{console.log(board)}}>save map</button>
      <button onClick={submitMap}>save map and play</button>
      </div>
      }

    </div>
    </div>
  )
}

export default Create;
