export default (row, col, parts, stones, numbersRight, numbersBottom) => {
    let board = [];
    let mineLocation = [];
   //üres board kreálás
    for (let x = 0; x < row; x++) {
      let subCol = [];
      for (let y = 0; y < col; y++) {
        subCol.push({
          value: 0,
          revealed: false,
          x: x,
          y: y,
          flagged: false,
        });
      }
      board.push(subCol);
    }
  //testrészek hozzá adása farok-fej-test
  let partsArr = parts.split(',').map(Number);
  board[parseInt(partsArr[0])][parseInt(partsArr[1])].value = 3;
  board[parseInt(partsArr[2])][parseInt(partsArr[3])].value = 4;
  board[parseInt(partsArr[4])][parseInt(partsArr[5])].value = 5;
  //kövek hozzá adása
  let stonesArr = stones.split(',').map(Number);
  for (let i=0; i < stonesArr.length; i++)
    {
      if(i % 2 != 0){
      board[parseInt(stonesArr[i])][parseInt(stonesArr[i-1])].value = 2; 
      }
    }
  //oldalsó,alsó számok fel botása
  let bottom = numbersRight.split(',').map(Number);
  let right = numbersBottom.split(',').map(Number);

    return { board, right, bottom };
  };
