export default (row, col) => {
    let board = [];
    let right = [col];
    let bottom = [row];
   //üres board kreálás
    for (let x = 0; x < row; x++) {
      let subCol = [];
      for (let y = 0; y < col; y++) {
        subCol.push({
          value: 0,
          active: false,
          x: x,
          y: y,
          
        });
      }
      board.push(subCol);
    }
  

    return { board, right, bottom };
  };
