export default (row, col, bombs) => {
    let board = [];
    let mineLocation = [];
    // Create blank board
  
    // x = column
    for (let x = 0; x < row; x++) {
      let subCol = [];
      for (let y = 0; y < col; y++) {
        subCol.push({
          value: 1,
          revealed: false,
          x: x,
          y: y,
          flagged: false,
        });
      }
      board.push(subCol);
    }
  
    
    return { board };
  };
