import React,{useEffect, useState, useContext} from 'react'
import createBoard from '../createBoard';
import Tile from './Tile'
import Cell from './Cell'
import BottomCells from './BottomCells';
import { ChosenMap } from '../ChosenMap';
import Axios from "axios";

const Board = () => {
  const {chosenMap, setChosenMap} = useContext(ChosenMap);
  const [grid,setGrid] = useState([]);
  const [lastPos,setLastPos] = useState([0,0]);
  const [BottomSide,setBottomSide] = useState([0]);
  const [rightSide,setRightSide] = useState([0]);

  const [aiBoard,setAIBoard] = useState([]);
  const [aiLastPos,setAiiLastPos] = useState([]);
//létre hozzuk a boardot
  useEffect(() =>{
    let width = 5
    let height = 5
    let parts = "0,0,0,1,0,2,"
    let stones = "1,0,1,2,1,4,3,0,"
    let right = "3,0,0,0,0"
    let bottom = "1,1,1,0,0"
    function mapGetter(){
      Axios.get(`http://localhost:3001/api/loadMap/${chosenMap}`).then((response) =>{
      width = response.data[0].width
      height = response.data[0].height
      parts = response.data[0].parts
      stones = response.data[0].stones
      right = response.data[0].rightS
      bottom = response.data[0].bottom

      function freshBoard(){
        const newBoard = createBoard(width, height, parts, stones, right, bottom);
        setAIBoard(newBoard);
        setGrid(newBoard.board);
        setBottomSide(newBoard.bottom);
        setRightSide(newBoard.right);

        function getStarterPos(){
         newBoard.board.map((singleRow,index1)=>{ 
          singleRow.map((singleBlock,index2)=>{ 
           if(singleBlock.value === 1){
             setLastPos([index1,index2])
             setAiiLastPos([index1,index2])
           }
          })
         })


        }getStarterPos();
      }freshBoard();
      }); 
    }mapGetter();
    
  }, []);
  useEffect(() =>{
    let rightCount = 0;
    let bottomCount = 0;
    let bodypartCount = 0;
    //oldalsó check
    grid.map((singleRow,index1)=>{   
      let rowCount = 0; 
        singleRow.map((singleBlock,index2)=>{
         if(singleBlock.value === 1){
          rowCount++;
         } 
        })
      if(rowCount === rightSide[index1]){
        rightCount++;
      }
    })
    //alsó check
    if(BottomSide.length>1){
      for(let i = 0; i < BottomSide.length;i++){
        let colCount = 0; 
        for(let j = 0; j < rightSide.length;j++){
          if(grid[j][i].value === 1){
            colCount++;
           } 
        }
        if(colCount === BottomSide[i]){
          bottomCount++;
        }
      }
    }
    //testresz check
    grid.map((singleRow,index1)=>{   
        singleRow.map((singleBlock,index2)=>{
         if(singleBlock.value === 3){
          bodypartCount++
         } 
         if(singleBlock.value === 4){
          bodypartCount++
         } 
        })
      
    })
    if( rightCount === rightSide.length && bottomCount === BottomSide.length && bodypartCount === 0){
      alert('win')
    }
  }, [grid]);
  
  

  //mezőre kattintás
  const tileClick = (x,y) =>{
    let newGrid = JSON.parse(JSON.stringify(grid));
    let elozo = lastPos;
    if(x === elozo[0]){
    if(newGrid[x][y].value === 0 || newGrid[x][y].value === 3 ||newGrid[x][y].value === 4 ){
      if(y + 1 === elozo[1] || y - 1 === elozo[1]){
        newGrid[x][y].value = 1;
        elozo[1] = y;
        setGrid(newGrid);
        
      }   
    }
  }
  if( y === elozo[1]){
    if(newGrid[x][y].value === 0 || newGrid[x][y].value === 3 ||newGrid[x][y].value === 4 ){
      if(x + 1 === elozo[0] || x - 1 === elozo[0]){
        newGrid[x][y].value = 1;
        elozo[0] = x;
        setGrid(newGrid);
    }   
    }
    setLastPos(elozo);
  }
  }
//Mestint
  const solver=()=>{
    
    var palya = aiBoard
    palya.pozicio = aiLastPos
    var adjList = [];
    var possibleMoves =[
      [-1,0], //up
      [1,0], //down
      [0,1], //right
      [0,-1], //left
    ];
    function getPossbleMoves(){
      if(palya.pozicio[0] !== 0){
        if(palya.board[palya.pozicio[0] - 1][palya.pozicio[1]].value !== 2 ){
        var up = palya
        up.board[palya.pozicio[0] - 1][palya.pozicio[1]].value = 1;
        up.pozicio[0] = palya.pozicio[0] - 1;
        adjList.push(up);
        }
      }
      if(palya.pozicio[1] !== 0){
        if(palya.board[palya.pozicio[0]][palya.pozicio[1] - 1].value !== 2){
          var left = palya
          left.board[palya.pozicio[0]][palya.pozicio[1] - 1].value = 1;
          left.pozicio[1] = left.pozicio[1] - 1;
          function addToList(){
            adjList.push(left);
          }addToList()
          
        }
      }
      if(palya.pozicio[0] !== palya.right.length){
          if(palya.board[palya.pozicio[0] + 1][palya.pozicio[1]].value !== 2 ){
            var down = palya;
            down.board[palya.pozicio[0] + 1][palya.pozicio[1]].value = 1;
            down.pozicio[0] = palya.pozicio[0] + 1;
            adjList.push(down);
          }   
        }
      if(palya.pozicio[1] !== palya.bottom.length){
          if(palya.board[palya.pozicio[0]][palya.pozicio[1] + 1].value !== 2){
              var right = palya;
              right.board[palya.pozicio[0]][palya.pozicio[1] + 1].value = 1;
              right.pozicio[1] = palya.pozicio[1] + 1;
              adjList.push(right);
          }
      }
      console.log(adjList)
    }getPossbleMoves();

  }


   //renderelés
    return ( <div>
    {
      
    grid.map((singleRow,index1)=>{
      return <div style={{display: "flex"}} key={index1}>{
        singleRow.map((singleBlock,index2)=>{
          return <Tile details={singleBlock} tileClick={tileClick} key={index2}/>;
        })
      }
           <Cell value={rightSide[index1]}/>    
      </div>
      
      
    })
    
    }
    <BottomCells values={BottomSide}/>
    <div>
      <button onClick={solver}>Solve with AI</button>
    </div>
    </div>
    )
}


export default Board;
